import React, { useState } from 'react';
import './profil.css';

const Profile = () => {
  const [profilePicture, setProfilePicture] = useState('/default-profile.png');
  const [participatedEvents, setParticipatedEvents] = useState(['Clean-Up Beach', 'Plant Trees']);
  const [allEvents, setAllEvents] = useState([
    { id: 1, title: 'Recycling Workshop', status: '' },
    { id: 2, title: 'Charity Run', status: '' },
    { id: 3, title: 'Environmental Conference', status: '' },
  ]);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfilePicture(URL.createObjectURL(file));
    }
  };

  const handleEventClick = (id, action) => {
    setAllEvents((prevEvents) =>
      prevEvents.map((event) =>
        event.id === id ? { ...event, status: action } : event
      )
    );
    if (action === 'Participate') {
      const participatedEvent = allEvents.find((event) => event.id === id).title;
      if (!participatedEvents.includes(participatedEvent)) {
        setParticipatedEvents([...participatedEvents, participatedEvent]);
      }
    }
  };

  return (
    <div>
      <Header />
    <div className="profile-container">
      <h1 className="profile-title">Mon Profil</h1>
      <div className="profile-card">
        <div className="profile-image">
          <img src={profilePicture} alt="Profile" />
          <label className="edit-image-label">
            Modifier
            <input type="file" onChange={handleImageChange} />
          </label>
        </div>
        <div className="profile-details">
          <h2>Nom de l'utilisateur</h2>
          <p><strong>ID:</strong> 12345</p>
          <p><strong>Email:</strong> utilisateur@example.com</p>
        </div>
      </div>

      <div className="profile-section">
        <h2>Événements auxquels j'ai participé</h2>
        {participatedEvents.length > 0 ? (
          <ul>
            {participatedEvents.map((event, index) => (
              <li key={index}>{event}</li>
            ))}
          </ul>
        ) : (
          <p>Vous n'avez pas encore participé à des événements.</p>
        )}
      </div>

      <div className="profile-section">
        <h2>Tous les Événements</h2>
        {allEvents.length > 0 ? (
          <ul className="event-list">
            {allEvents.map((event) => (
              <li key={event.id}>
                <span>{event.title}</span>
                <button
                  className="action-button participate"
                  onClick={() => handleEventClick(event.id, 'Participate')}
                >
                  Participer
                </button>
                <button
                  className="action-button interested"
                  onClick={() => handleEventClick(event.id, 'Interested')}
                >
                  Intéressé
                </button>
              </li>
            ))}
          </ul>
        ) : (
          <p>Pas d'événements disponibles.</p>
        )}
      </div>
    </div>
    </div>
  );
};

export default Profile;
