import React, { useState } from 'react';
import Header from '../Header/Header';
import Footer from '../Footer/Footer';
import styles from './acceuil.module.css';
import gauche from './gauche.png';
import droite from './droite.png'
import Slide1 from './Slide1.png'
const Acceuil = () => {
  const images = [
    './Slide1.png',
    './Slide2.png',
    './Slide3.png',
    './Slide4.png',
  ];

  const [currentSlide, setCurrentSlide] = useState(0);

  const nextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % images.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prevSlide) =>
      prevSlide === 0 ? images.length - 1 : prevSlide - 1
    );
  };

  return (
    <div>
      <Header />
      <div className={styles.sliderContainer}>
        <img alt="" src ={gauche} className={styles.arrow} onClick={prevSlide}/>
          &#9664; {/* Left Arrow */}
      
        <div className={styles.slider}>
          <img
            src={images[currentSlide]}
            alt={`Slide ${currentSlide + 1}`}
            className={styles.slideImage}
          />
        </div>
        <img alt="" src={droite} className={styles.arrow} onClick={nextSlide}/>
          &#9654; {/* Right Arrow */}
        
      </div>
      <Footer />
    </div>
  );
};

export default Acceuil;
