import React, { useState } from "react";
import Header from "../Header/Header";
import Footer from "../Footer/Footer";
import "./organizer.css";

const Organizer = () => {
  const [events, setEvents] = useState([]);
  const [formData, setFormData] = useState({
    type: "",
    location: "",
    date: "",
    title: "",
    description: "",
    organizer: "",
    poster: null,
  });

  const [editIndex, setEditIndex] = useState(-1);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleFileChange = (e) => {
    setFormData((prev) => ({ ...prev, poster: e.target.files[0] }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (editIndex !== -1) {
      const updatedEvents = [...events];
      updatedEvents[editIndex] = { ...formData };
      setEvents(updatedEvents);
      setEditIndex(-1);
    } else {
      setEvents([...events, formData]);
    }
    setFormData({
      type: "",
      location: "",
      date: "",
      title: "",
      description: "",
      organizer: "",
      poster: null,
    });
  };

  const handleEdit = (index) => {
    setFormData(events[index]);
    setEditIndex(index);
  };

  const handleDelete = (index) => {
    setEvents(events.filter((_, i) => i !== index));
  };

  const handleShare = (event) => {
    alert(`Sharing event: ${event.title}`);
  };

  return (
    <div>
      <Header />
      <div className="organizer-container">
        <h1>Organize Your Event</h1>
        <form className="event-form" onSubmit={handleSubmit}>
          <input
            type="text"
            name="title"
            value={formData.title}
            onChange={handleChange}
            placeholder="Event Title"
            required
          />
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            placeholder="Event Description"
            required
          ></textarea>
          <input
            type="text"
            name="organizer"
            value={formData.organizer}
            onChange={handleChange}
            placeholder="Organizer Name"
            required
          />
          <select name="type" value={formData.type} onChange={handleChange}>
            <option value="">Select Event Type</option>
            <option value="Conference">Conference</option>
            <option value="Workshop">Workshop</option>
            <option value="Clean-up">Clean-up</option>
          </select>
          <input
            type="text"
            name="location"
            value={formData.location}
            onChange={handleChange}
            placeholder="Location"
            required
          />
          <input
            type="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            required
          />
          <input type="file" onChange={handleFileChange} />
          <button type="submit">{editIndex !== -1 ? "Modify" : "Validate"}</button>
        </form>

        <table className="event-table">
          <thead>
            <tr>
              <th>Poster</th>
              <th>Title</th>
              <th>Description</th>
              <th>Organizer</th>
              <th>Type</th>
              <th>Location</th>
              <th>Date</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {events.map((event, index) => (
              <tr key={index}>
                <td>
                  {event.poster && (
                    <img
                      src={URL.createObjectURL(event.poster)}
                      alt={event.title}
                      className="poster-image"
                    />
                  )}
                </td>
                <td>{event.title}</td>
                <td>{event.description}</td>
                <td>{event.organizer}</td>
                <td>{event.type}</td>
                <td>{event.location}</td>
                <td>{event.date}</td>
                <td>
                  <button onClick={() => handleEdit(index)}>Edit</button>
                  <button onClick={() => handleDelete(index)}>Cancel</button>
                  <button onClick={() => handleShare(event)}>Share</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <Footer />
    </div>
  );
};

export default Organizer;
